operation.search:
// Do a few things to protect against missing settings or settings with invalid values...
if (empty($modSettings['defaultMaxTopics']) || $modSettings['defaultMaxTopics'] <= 0 || $modSettings['defaultMaxTopics'] > 999)
	$modSettings['defaultMaxTopics'] = 20;
if (empty($modSettings['defaultMaxMessages']) || $modSettings['defaultMaxMessages'] <= 0 || $modSettings['defaultMaxMessages'] > 999)
	$modSettings['defaultMaxMessages'] = 15;
if (empty($modSettings['defaultMaxMembers']) || $modSettings['defaultMaxMembers'] <= 0 || $modSettings['defaultMaxMembers'] > 999)
	$modSettings['defaultMaxMembers'] = 30;

operation.add:
// Smart Cache
if (!empty($modSettings['load_average'])) {
	$modSettings['cache_load_time'] = cache_get_data('load_time', 90);
	if($modSettings['load_average'] > 5 && $modSettings['cache_enable'] < 2 || !empty($modSettings['cache_load_time']) && $modSettings['cache_load_time'] >= 0.5 && $modSettings['cache_load_time'] < 1)
		$modSettings['cache_enable'] = 2;
	if($modSettings['load_average'] > 7 && $modSettings['cache_enable'] < 3 || !empty($modSettings['cache_load_time']) && $modSettings['cache_load_time'] >= 1)
		$modSettings['cache_enable'] = 3;
}

operation.search:
// Show the load time?  (only makes sense for the footer.)
$context['show_load_time'] = !empty($modSettings['timeLoadPageEnable']);
$context['load_time'] = round(array_sum(explode(' ', microtime())) - array_sum(explode(' ', $time_start)), 3);
$context['load_queries'] = $db_count;
